create table customers(
	customer_id        serial primary key,
	username           varchar not null,
	password           varchar not null,
	name			   varchar not null,
	phone			   varchar not null, 
	email			   varchar
);

create table address(
	address_id		   serial primary key,
	delivary_address   varchar,
	lat				   real,
	long			   real,
	customer_id        integer not null,
	constraint fk_customers_address foreign key(customer_id) references customers(customer_id)
);

create table delivary_agent(
	person_id			serial primary key,
	name				varchar not null,
	availability		boolean not null, -- default false
	phone				varchar not null
);

create table restaurants(
	restaurant_id		serial primary key,
	name				varchar not null,
	phone				varchar,
	lat 				real,
	long				real,
	start_time          timestamp,
	close_time			timestamp,
	img					varchar
);
-- may add reviews table

create table foodItems(
	item_id				serial primary key,
	name				varchar not null,
	availability 		boolean default false,
	price 				real not null,
	restaurant_id		integer not null,
	description			varchar,
	category           	varchar,
	img			        varchar,
	constraint fk_item_restaruant foreign key(restaurant_id) references restaurants(restaurant_id)
);
-- may add categories

create table orders(
	order_id			serial primary key,
	order_time			timestamp not null,
	delivary_time		timestamp,
	total_price			real not null,
	distance			real,
	payment_done		boolean not null,
	delivary_status     varchar not null,
	restaurant_id		integer not null,
	agent_id			integer not null,
	constraint fk_orders_restaurant foreign key(restaurant_id) references restaurants(restaurant_id),
	constraint fk_orders_agent foreign key(agent_id) references delivary_agent(person_id)
);
-- may add delivery status

create table order_items(
	id					serial primary key,
	order_id			integer not null,
	item_id				integer not null,
	quantity			integer not null, 
	constraint fk_order_items_order foreign key(order_id) references orders(order_id),
	constraint fk_order_items_items foreign key(item_id) references foodItems(item_id)
);

